mapper.update:
UPDATE livingservice SET
update_date=now()

    ,name=#{name}


    ,description=#{description}


    ,type=#{type}


    ,status=#{status}


    ,payment=#{payment}


    ,price=#{price}


    ,priceUnit=#{priceUnit}


    ,special=#{special}


    ,information=#{information}


    ,sys_office_id=#{sys_office_id}

WHERE id = #{id}

mapper.select:
SELECT ls.*,so.name as officeName,count(lo.id) as count FROM `livingservice` ls
inner join sys_office so on so.id=ls.sys_office_id
left join livingservice_order lo on lo.livingservice_id=ls.id
where ls.del_flag='0' and ls.status='0'

    and ls.id=#{id}


    and ls.name=#{name}


    and ls.type=#{type}


    and so.id=#{sys_office_id}

group by ls.id
order by ls.update_date
limit #{lastNo},#{nextNo}

select so.id,so.logo,so.name,lof.about,lof.announcement,count(l.id) as serviceCount,so.address from sys_office so inner join livingService_office lof on so.id=lof.sys_office_id inner join livingservice l on so.id=l.sys_office_id where l.del_flag='0'
and so.id=#{id}
group by so.id

select count(loi.id) as count from livingservice l inner join livingservice_order lo on lo.livingservice_id=l.id inner join livingservice_order_information loi on lo.id=loi.livingservice_order_id where loi.`status`='2'
and l.sys_office_id=#{sysOfficeID}
group by l.id
